nasal.load:
        # Note: This nasal code is loaded just for remote multiplayer models

        var rplayer = cmdarg();

        # mark model as remote
        rplayer.getNode("sim/model/c182s/remote-instance", 1).setBoolValue(1);

        # Set up property aliases for animations.
        rplayer.getNode("sim/model/door-positions/DoorL/position-norm-effective", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[0]"));
        rplayer.getNode("sim/model/door-positions/DoorR/position-norm-effective", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[1]"));
        rplayer.getNode("sim/model/door-positions/WindowL/position-norm", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[2]"));
        rplayer.getNode("sim/model/door-positions/WindowR/position-norm", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[3]"));
        rplayer.getNode("sim/model/door-positions/BaggageDoor/position-norm-effective", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[4]"));
        rplayer.getNode("systems/electrical/outputs/landing-lights", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[5]"));
        rplayer.getNode("systems/electrical/outputs/taxi-light", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[6]"));
        # float[7]=strobe power source; float[8]=beacon power source: both are not aliased but used directly below
        rplayer.getNode("systems/electrical/outputs/nav-lights", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[9]"));
        rplayer.getNode("fdm/jsbsim/propulsion/engine[0]/cowl-flaps-norm", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[10]"));

        # Damage animations
        rplayer.getNode("fdm/jsbsim/gear/unit[0]/z-position", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[11]"));
        rplayer.getNode("fdm/jsbsim/gear/unit[1]/z-position", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[12]"));
        rplayer.getNode("fdm/jsbsim/gear/unit[2]/z-position", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[13]"));
        rplayer.getNode("engines/engine/crashed", 1).
            alias(rplayer.getNode("sim/multiplay/generic/float[14]"));
# Wing damage animation not yet implemented: poperties not known
#        rplayer.getNode("fdm/jsbsim/wing-damage/left-wing", 1).
#            alias(rplayer.getNode("sim/multiplay/generic/float[xx]"));
#        rplayer.getNode("fdm/jsbsim/wing-damage/right-wing", 1).
#            alias(rplayer.getNode("sim/multiplay/generic/float[xx]"));

        rplayer.getNode("sim/model/c182s/nw_fairing_lcl", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[0]"));
        rplayer.getNode("sim/model/c182s/mw_fairing_lcl", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[1]"));

        rplayer.getNode("sim/model/immat", 1).
            alias(rplayer.getNode("sim/multiplay/generic/string[0]"));


        # Beacon and strobes lighting
        # We create a local strobe/beacon instance that is only active, if the remote aircrafts strobe/beacon is active (power+functional+switched on)
        # This saves bandwith and the animation is smoother, but has de disadvantage of the strobe/beacon patterns being not in sync.
        var beacon_switch = rplayer.getNode("systems/electrical/beacon-switch");
        var lighting_beacon_state = rplayer.getNode("systems/electrical/beacon-state",1).getPath();
        var beacon_light = aircraft.light.new(lighting_beacon_state, [1.0, 1.0], beacon_switch);
        setlistener(rplayer.getNode("sim/multiplay/generic/float[8]").getPath(), func (node) {
            if (node.getValue() > 0) beacon_light.switch(1);
            if (node.getValue() <= 0) beacon_light.switch(0);
        }, 1, 0);
        var beacon_node      = rplayer.getNode("systems/electrical/outputs/beacon",1).getPath();
        var beacon_node_norm = rplayer.getNode("systems/electrical/outputs/beacon-norm",1).getPath();
        var beacon_comp_norm = rplayer.getNode("sim/model/c182s/lighting/beacon-dim-factor",1).getPath();
        setlistener(lighting_beacon_state ~ "/state", func (node) {
            var bus_volts = 0;
            if (node.getValue()) bus_volts = 24;
            interpolate (beacon_node, bus_volts, 1);
            interpolate (beacon_node_norm, (bus_volts/24), 1);
            interpolate (beacon_comp_norm, (bus_volts/24), 1);
        }, 1,0);

        var strobe_switch = rplayer.getNode("systems/electrical/strobe-switch");
        var lighting_strobes_state = rplayer.getNode("systems/electrical/strobe-state",1).getPath();
        var strobe_light = aircraft.light.new( lighting_strobes_state, [0.05, 0.05, 0.05, 1.7 ], strobe_switch );
        setlistener(rplayer.getNode("sim/multiplay/generic/float[7]").getPath(), func (node) {
            if (node.getValue() > 12) strobe_light.switch(1);
            if (node.getValue() <= 0) strobe_light.switch(0);
        }, 1, 0);
        var strobe_node      = rplayer.getNode("systems/electrical/outputs/strobe",1).getPath();
        var strobe_node_norm = rplayer.getNode("systems/electrical/outputs/strobe-norm",1).getPath();
        setlistener(lighting_strobes_state ~ "/state", func (node) {
            var bus_volts = 0;
            if (node.getValue()) bus_volts = 24;
            setprop(strobe_node, bus_volts);
            setprop(strobe_node_norm, bus_volts/24);
        }, 1, 0);

        # ALS light effect derived from the state of the lights
        props.globals.getNode("/sim/model/c182s/procedural-lights",1).setBoolValue(1);
        var nav_lights_norm = rplayer.getNode("systems/electrical/outputs/nav-lights-norm",1).getPath();
        setlistener(rplayer.getNode("sim/multiplay/generic/float[9]").getPath(), func (node) {
            if (node.getValue() > 12) setprop(nav_lights_norm, 1);
            if (node.getValue() <= 0) setprop(nav_lights_norm, 0);
        }, 1, 0);

        # calculate dim factor locally
        var dim_factor = rplayer.getNode("sim/model/c182s/lighting/dim-factor",1).getPath();
        var update_dim_factor = func() {
          var diffuse_red = getprop("/rendering/scene/diffuse/red");
          var dimval = 1 - diffuse_red;
          setprop(dim_factor, dimval);
          #print("c182s dim_factor update: "~ dim_factor ~ "=" ~ dimval ~ "(diffuse_red="~diffuse_red ~")");
        };
        update_dim_factor();
        dim_factor_calc = maketimer(5, update_dim_factor);
        dim_factor_calc.start();


        # Small helper function to update the wheel fairings etc.
        # They may change with livery change which doesn't get detectd using the normal MP protocol
        var updateLivery = func() {
            rplayer.getNode("sim/model/c182s/mw_fairing", 1).setBoolValue(rplayer.getNode("sim/model/c182s/mw_fairing_lcl").getBoolValue());
            rplayer.getNode("sim/model/c182s/nw_fairing", 1).setBoolValue(rplayer.getNode("sim/model/c182s/nw_fairing_lcl").getBoolValue());
        }

        # Wheel fairings change: in case new properties get supplied, change the fairings
        setlistener(rplayer.getNode("sim/multiplay/generic/bool[0]").getPath(), updateLivery, 1, 0);
        setlistener(rplayer.getNode("sim/multiplay/generic/bool[1]").getPath(), updateLivery, 1, 0);


        # Update the selected livery
        var livery_update = aircraft.livery_update.new(
            "Aircraft/c182s/Models/Liveries", 10,
            func {
                print("c182s livery update");
                updateLivery();
                set_registration_number(rplayer, rplayer.getNode("sim/multiplay/generic/string[0]").getValue());
            });


        # Show Registration
        io.include("Aircraft/c182s/Nasal/registration_number.nas");
        initC182ImmatCanvas(_module_id);
        var regnum_path = rplayer.getNode("sim/multiplay/generic/string[0]").getPath();
        setlistener(regnum_path, func (node) {
            set_registration_number(rplayer, node.getValue());
        }, 1, 0);


        # Show the passengers in multiplayer models
        rplayer.getNode("pax/co-pilot/present", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[2]"));
        rplayer.getNode("pax/left-passenger/present", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[3]"));
        rplayer.getNode("pax/right-passenger/present", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[4]"));
        rplayer.getNode("sim/model/c182s/parachuters/aircraft-mod-enabled", 1).
            alias(rplayer.getNode("sim/multiplay/generic/bool[20]"));
        setlistener(rplayer.getNode("sim/multiplay/generic/bool[20]").getPath(), func (node) {
            var pax_modelcfg = node.getBoolValue()? "humans-pax-parachuters.xml" : "humans.xml";
            io.read_properties ("Aircraft/c182s/Models/Human/"~pax_modelcfg, rplayer.getNode ("sim/model"));
        }, 1, 0);


        # Slow MP properties
        if (!defined("dual_control_tools")) {
            # Dual control tools library is not loaded by default, so load it now (from localy installed FGData)
            #print("C182 MP extended slow properties: loading dual-control-tools.nas");
            io.load_nasal(getprop("/sim/fg-root")~"/Aircraft/Generic/DualControl/dual-control-tools.nas", "dual_control_tools");
        }
        var DCT = dual_control_tools;
        var c182_mp_slowprop_TDM1 = DCT.TDMDecoder.new( rplayer.getNode("sim/multiplay/generic/string[7]"), [
            func (v) { rplayer.getNode("gear/gear[0]/rollspeed-ms",1).setValue(v)  },
            func (v) { rplayer.getNode("gear/gear[1]/rollspeed-ms",1).setValue(v)  },
            func (v) { rplayer.getNode("gear/gear[2]/rollspeed-ms",1).setValue(v)  },
            func (v) { rplayer.getNode("gear/gear/steering-norm",1).setValue(v)  },
            func (v) { rplayer.getNode("fdm/jsbsim/ice/graphic",1).setValue(v)  },
        ]);
        c182_mp_slowprop_TDM1_updater = maketimer(0.25, func{
            #print("C182 MP extended slow properties TDM1 decoder update()");
            c182_mp_slowprop_TDM1.update();
        });
        c182_mp_slowprop_TDM1_updater.start();


        # Parachuters dropping animation
        io.include("Aircraft/c182s/Nasal/mp-parachuters.nas");
        setlistener(rplayer.getNode("sim/multiplay/generic/bool[21]").getPath(), func(node) {
            if (node.getValue() == 1) mp_parachuterjump_spawn(rplayer);
        }, 0, 0);

binding.script:
var cover = "On";
if (!getprop("/sim/model/c182s/securing/pitot-cover-visible")) {
    cover = "Off";
}

var heat = "cold";
if (getprop("/systems/pitot/heat-applied-norm") > 0.75) {
    heat = "warm";
}
var returnString = "Pitot-Cover: " ~ cover ~ "\nTouch: " ~ heat;
return returnString;
