binding.script:
var cover = "On";
if (!getprop("/sim/model/c182s/securing/pitot-cover-visible")) {
    cover = "Off";
}

var heat = "cold";
if (getprop("/systems/pitot/heat-applied-norm") > 0.75) {
    heat = "warm";
}
var returnString = "Pitot-Cover: " ~ cover ~ "\nTouch: " ~ heat;
return returnString;
