binding.script:

var setting     = math.round(getprop("/environment/aircraft-effects/cabin-heat-set") * 100);
var heatChange  = getprop("/fdm/jsbsim/heat/cabin-temperature-change-text") or "";
var temptxt     = getprop("/fdm/jsbsim/heat/cabin-temperature-text-short") or "";
if (heatChange != "") heatChange = "; " ~ heatChange;
var heatTxt     = "\n(" ~ temptxt ~ heatChange ~ ")";
var returnString = "Cabin Heat: " ~ setting ~ "%\n" ~ heatTxt;
return returnString;


var setting     = math.round(getprop("/environment/aircraft-effects/cabin-air-set") * 100);
var heatChange  = getprop("/fdm/jsbsim/heat/cabin-temperature-change-text") or "";
var temptxt     = getprop("/fdm/jsbsim/heat/cabin-temperature-text-short") or "";
if (heatChange != "") heatChange = "; " ~ heatChange;
var heatTxt     = "\n(" ~ temptxt ~ heatChange ~ ")";
var returnString = "Cabin Air: " ~ setting ~ "%\n" ~ heatTxt;
return returnString;


var setting     = getprop("controls/engines/engine/master-alt");
var setting_txt = "OFF";  if (setting) setting_txt = "ON";
var ret_str     = setting_txt;
if (getprop("sim/model/split-master-switches"))  ret_str ~= "\n(shift-click for both switches)";
if (!getprop("sim/model/split-master-switches")) ret_str ~= "\n(shift-click for single switch)";
return ret_str;


var setting     = getprop("controls/engines/engine/master-bat");
var setting_txt = "OFF";  if (setting) setting_txt = "ON";
var ret_str     = setting_txt;
if (getprop("sim/model/split-master-switches"))  ret_str ~= "\n(shift-click for both switches)";
if (!getprop("sim/model/split-master-switches")) ret_str ~= "\n(shift-click for single switch)";
return ret_str;


var setting     = getprop("controls/switches/AVMBus1");
var setting_txt = "OFF";  if (setting) setting_txt = "ON";
var ret_str     = setting_txt;
if (getprop("sim/model/split-master-switches"))  ret_str ~= "\n(shift-click for both switches)";
if (!getprop("sim/model/split-master-switches")) ret_str ~= "\n(shift-click for single switch)";
return ret_str;


var setting     = getprop("controls/switches/AVMBus2");
var setting_txt = "OFF";  if (setting) setting_txt = "ON";
var ret_str     = setting_txt;
if (getprop("sim/model/split-master-switches"))  ret_str ~= "\n(shift-click for both switches)";
if (!getprop("sim/model/split-master-switches")) ret_str ~= "\n(shift-click for single switch)";
return ret_str;

var m = arg[0];
if (m == 1) return 'RIGHT';
if (m == 2) return 'LEFT';
if (m == 3) return 'BOTH';
return 'OFF';

var fsp     = "controls/switches/fuel_tank_selector";
var fsp_tgt = "controls/switches/fuel_tank_selector_tgt";

# Push selector down, then rotate and release the push
var pushTime = 0.2;
interpolate(
    "sim/model/c182s/cockpit/fuel_tank_selector-z",
    1,
pushTime);

var rotateTime = 0.5;
var rotateAction = maketimer(pushTime, func {
    interpolate(fsp, getprop(fsp_tgt), rotateTime);
});
rotateAction.singleShot = 1;
rotateAction.start();

var releaseTime = 0.1;
var releaseAction = maketimer(pushTime+rotateTime, func {
    interpolate(
        "sim/model/c182s/cockpit/fuel_tank_selector-z",
        0,
    releaseTime);
});
releaseAction.singleShot = 1;
releaseAction.start();

var cleanupTime = 0.1;
var cleanupAction = maketimer(pushTime+rotateTime+releaseTime, func {
    if (getprop(fsp) < 0) setprop(fsp, 3);
    if (getprop(fsp) > 3) setprop(fsp, 0);
});
cleanupAction.singleShot = 1;
cleanupAction.start();

var m = arg[0];
if (m == 1) return 'RIGHT';
if (m == 2) return 'BOTH';
if (m == 3) return 'LEFT';
return 'OFF';

if (arg[0] == 0) return "center";
if (arg[0] > 0)  return arg[0]*100 ~ " cm back";
if (arg[0] < 0)  return arg[0]*-100 ~ " cm front";
